var feedbackLink=document.querySelector(".btn-feedback"),feedbackPopup=document.querySelector(".modal-feedback"),feedbackClose=feedbackPopup.querySelector(".modal-close"),form=feedbackPopup.querySelector("form"),username=feedbackPopup.querySelector("[name=username]"),email=feedbackPopup.querySelector("[name=email]"),letter=feedbackPopup.querySelector("[name=letter]");feedbackLink.addEventListener("click",function(e){e.preventDefault(),feedbackPopup.classList.add("modal-show"),username.focus()}),feedbackClose.addEventListener("click",function(e){e.preventDefault(),feedbackPopup.classList.remove("modal-show")}),form.addEventListener("submit",function(e){username.value&&email.value&&letter.value||(e.preventDefault(),username.classList.add("invalid"),email.classList.add("invalid"),letter.classList.add("invalid"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),feedbackPopup.classList.contains("modal-show")&&feedbackPopup.classList.remove("modal-show"))});
